
(function(window, SF, $, undefined) { 
    
    // ------------------------------------------------------------------------
    //                      NAV
    //                      
    //                      depends:
    //                          b.base.js
    //                      uses:
    //                          sf-base_uri.js
    // ------------------------------------------------------------------------
    /** nav namespace **/
    SF.nav = SF.nav||{};
    
    /**
     * Navigate to destination.
     * @destination (string)
     * @query (string or object)
     */
    SF.nav.go = function (/*string*/destination, /*string, object*/query){
        if(query&&typeof(query)==="object"){
            query = SF.toQueryString(query);
        }
        var path = SF.uri.navpath(destination).concat(query?"?"+query:""); 
        
        window.location.href = path;
    };
    
    SF.nav.reload = function (/*string, object*/query){
        SF.nav.go(SF.uri.current().attr.path, query);
    };
    
    
    
})(window, smartforge, jQuery);
