var assert = require('assert')
    , application = require('../../lib/application')
    , _application = require('../../lib/application/_application.js')
    ;

// --------------------------------------------------------------------------------------------------------------
//                              logging
// --------------------------------------------------------------------------------------------------------------

application.setLogLevel('fine');

application.error('test');
application.error('hello world');
application.error('counter', 123);
application.error(1, "2", [ 3, 4 ], { 5: 6 }, function() { return 7; });
application.warning('test');
application.warning('hello world');
application.warning('counter', 123);
application.warning(1, "2", [ 3, 4 ], { 5: 6 }, function() { return 7; });
application.info('test');
application.info('hello world');
application.info('counter', 123);
application.info(1, "2", [ 3, 4 ], { 5: 6 }, function() { return 7; });
application.fine('test');
application.fine('hello world');
application.fine('counter', 123);
application.fine(1, "2", [ 3, 4 ], { 5: 6 }, function() { return 7; });

// --------------------------------------------------------------------------------------------------------------
//                              middleware
// --------------------------------------------------------------------------------------------------------------

var redir301 = application.middleware.redirect301;
assert.ok(null!=redir301);
application.info(redir301);