var assert = require('assert')
    , logging = require('../../lib/module_logging/logging.js')
    ;

var test_singleton = true
    , test_instance = true;

if(test_singleton){
    // uses singleton logger
    logging.log('hello world');
    logging.log('counter', 123);
    logging.log('global variables', global);
    logging.log(1, "2", [ 3, 4 ], { 5: 6 }, function() { return 7; });

    // log level
    logging.setLevel('fine');
    logging.log('error', 'this is an error');
    logging.log('warning', 'this is  warning');
    logging.log('info', 'this is an info');
    logging.log('fine', 'this is a debug message');
}

if(test_instance){
    // uses logger instance
    var logger = new logging.Logger(__filename);
    logger.log('hello world');
    logger.log('counter', 123);
    logger.log('global variables', global);
    logger.log(1, "2", [ 3, 4 ], { 5: 6 }, function() { return 7; });

    logger.error('hello world');
    logger.warning('hello world');
    logger.info('hello world');
    logger.fine('hello world');
}
